using System;using System.Linq;public class Program{public static void Main(){int N = int.Parse(Console.ReadLine());string[] arr = Console.ReadLine().Split();int[] intArr = new int[N];int i = 0;while(i < N){intArr[i] = int.Parse(arr[i]);i++;}double max = intArr.Max();Console.WriteLine(intArr.Average()/max*100);}}